import React, {useState, useCallback, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { csv, scaleLinear, max, scaleTime, extent, timeFormat } from 'd3';
import { useData } from './useData';
import { AxisBottom } from './AxisBottom';
import { AxisLeft } from './AxisLeft';
import { Marks } from './Marks';
import './style.css';

const width = 960;
const height = 500;
const margin = { top: 20, right: 20, bottom: 60, left: 60 };
const AxisLabelOffset = 40;
const AxisOffset = 7;
const circleRadius = 3;

const App = () => {
    const data = useData();

    if (!data) {
        return <div>Loading...</div>;
    }

    const innerHeight = height - margin.top - margin.bottom;
    const innerWidth = width - margin.left - margin.right;

    const xValue = d => d.timestamp;
    const xAxisLabel = 'Time';

    const yValue = d => d.temperature;
    const yAxisLabel = 'Temperature in [°C]';

    const xAxisTickFormat = timeFormat("%a");
    const roundToTwo = d => +(Math.round(d + "e+2")  + "e-2");    

    const xScale = scaleLinear()
        .domain(extent(data, xValue))
        .range([0, innerWidth])
        .nice();

    const yScale = scaleLinear()
        .domain(extent(data, yValue))
        .range([innerHeight - 5, 0])
        .nice(); 

    return(
        <>
            <h1 className="header">Temperature</h1>
            <svg className="plot" width={width} height={height}>
                <g transform={`translate(${margin.left}, ${margin.top})`}>
                    <AxisBottom 
                        xScale={xScale} 
                        innerHeight={innerHeight} 
                        tickFormat={xAxisTickFormat}
                        tickOffset={AxisOffset}
                        />
                    <text
                        className="axis-label"
                        x={innerWidth / 2}
                        y={innerHeight + AxisLabelOffset}
                        textAnchor="middle"
                    >             
                        {xAxisLabel}
                    </text>                
                    <AxisLeft 
                        yScale={yScale} 
                        innerWidth={innerWidth} 
                        tickOffset={AxisOffset}
                    />
                    <text
                        className="axis-label"
                        textAnchor="middle"
                        transform={'rotate(-90)'}
                        dx = { -innerHeight / 2 }
                        dy = { -AxisLabelOffset }
                    >
                        {yAxisLabel}
                    </text>
                    <Marks 
                        data={data} 
                        xScale={xScale} 
                        yScale={yScale}
                        xValue={xValue}
                        yValue={yValue}
                        tooltipFormat={roundToTwo}
                        circleRadius={circleRadius}
                        />
                </g>
            </svg>
        </>
        );
    };
const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
